
import type {
    BrowserWallet,
    WalletModule,
    WalletModuleFactory,
} from '@near-wallet-selector/core';
import { BitteWallet } from './bitte-wallet';
import { resolveWalletUrl } from './utils';
  
  interface BitteWalletSetup {
    callbackUrl?: string;
    successUrl?: string;
    walletUrl?: string;
    failureUrl?: string;
    deprecated?: boolean;
    contractId?: string;
    lak?: boolean;
  }
  
  const icon =
    'data:image/svg+xml;base64,';
  
  export function setupBitteWallet({
    walletUrl = '',
    deprecated = false,
    successUrl = '',
    failureUrl = '',
    callbackUrl = '',
    contractId = '',
  }: BitteWalletSetup = {}): WalletModuleFactory<BrowserWallet> {
  
    return async (moduleOptions): Promise<WalletModule<BrowserWallet> | null> => {
  
      const wallet: WalletModule<BrowserWallet> = {
        id: 'mintbase-wallet',
        type: 'browser',
        metadata: {
          name: 'Mintbase Wallet',
          description:
            'NEAR wallet to store, buy, send and stake assets for DeFi.',
          iconUrl: icon,
          deprecated,
          available: true,
          successUrl,
          failureUrl,
          walletUrl: resolveWalletUrl(moduleOptions.options.network.networkId, walletUrl),
        },
        init: (options) => {
          return BitteWallet({ callback: callbackUrl, networkId: moduleOptions.options.network.networkId, successUrl, failureUrl, contractId,  ...options });
        },
      };
      return wallet;
    };
  }
  